// Menu.js
import React, { useState } from 'react';
import MenuItem from './MenuItem';
import './menu.css';
import './App.css';

const menuData = [
  {
    name: 'Original Recipe Chicken',
    description: 'KFC\'s original recipe chicken, crispy and delicious.',
    image: 'https://img.freepik.com/free-photo/fried-chicken-french-fries-black-cement-floor_1150-28542.jpg?size=626&ext=jpg&ga=GA1.1.1826414947.1699315200&semt=ais',
    price: 9.99,
  },
  {
    name: 'Zinger Burger',
    description: 'Spicy chicken fillet, lettuce, and mayo in a soft bun.',
    image: 'data:image/jpeg;base64,',
    price: 6.99,
  },
  {
    name: 'Mashed Potatoes',
    description: 'Creamy mashed potatoes with KFC\'s signature gravy.',
    image: 'data:image/jpeg;base64,',
    price: 3.49,
  },
  {
    name: 'Twister Wrap',
    description: 'Grilled chicken, lettuce, and salsa wrapped in a tortilla.',
    image: 'data:image/jpeg;base64,',
    price: 5.99,
  },
  {
    name: 'Fries',
    description: 'Golden and crispy KFC fries.',
    image: 'data:image/jpeg;base64,',
    price: 2.49,
  },
  {
    name: 'Cole Slaw',
    description: 'Freshly shredded cabbage and carrots in a creamy dressing.',
    image: 'data:image/jpeg;base64,',
    price: 1.99,
  },
];

const Menu = () => {
  const [cart, setCart] = useState([]);

  const addToCart = (item) => {
    setCart([...cart, item]);
  };

  const removeFromCart = (itemToRemove) => {
    const updatedCart = cart.filter((cartItem) => cartItem !== itemToRemove);
    setCart(updatedCart);
  };

  const calculateTotal = () => {
    return cart.reduce((total, item) => total + item.price, 0);
  };

  return (
    <div>
      <h2>EVERY DAY'S HAPPINESS</h2>
      <div className="menu-container">
        {menuData.map((item, index) => (
          <MenuItem
            key={index}
            item={item}
            addToCart={addToCart}
            removeFromCart={removeFromCart}
            isInCart={cart.includes(item)}
          />
        ))}
      </div>
      <div className="cart-summary">
        <h3>Shopping Cart</h3>
        <ul>
          {cart.map((cartItem, index) => (
            <li key={index}>{cartItem.name} - ${cartItem.price.toFixed(2)}</li>
          ))}
        </ul>
        <p>Total: ${calculateTotal().toFixed(2)}</p>
      </div>
    </div>
  );
};

export default Menu;
